mapper.select:

where

	g.student_id = #{studentId}


	AND g.studentName like concat('%', #{studentName}, '%')


	AND g.sex =#{sex}


	AND g.whereabouts = #{whereabouts}


	AND g.cause = #{cause}

select g.student_id, g.studentName, g.sex, g.phonenumber, g.homeAddress, g.avatarPath, g.whereabouts, g.cause, g.post, g.company, r.resume_id from (SELECT * from graduate g
WHERE g.studentName like concat('%', #{lookup}, '%') or g.student_id like concat('%', #{lookup}, '%')
) g LEFT JOIN resume r on g.student_id=r.student_id

mapper.update:
update Graduate
studentName=#{studentName}, sex=#{sex}, phonenumber=#{phonenumber}, homeAddress=#{homeAddress}, whereabouts=#{whereabouts}, cause=#{cause}, post=#{post}, company=#{company}, password=#{password}
where student_id = #{studentId}
